export class PiecesJointeComponent implements OnInit, AfterViewInit {
  layoutCtrl = new UntypedFormControl('boxed');

  subject$: ReplaySubject<PiecesJointe[]> = new ReplaySubject<PiecesJointe[]>(1);
  data$: Observable<PiecesJointe[]> = this.subject$.asObservable();

  @Output() newItemEvent = new EventEmitter<[string, Object]>();
  keyOfForm:string = "contact"

  listOfPjs:any = []
  addChangeEvent() {
    this.newItemEvent.emit([this.keyOfForm, this.listOfPjs]);
  }

  ngOnChanges(changes: SimpleChanges) {
    try{
      this.subject$.next(this.listOfPjs);
    }catch(e){}
  }

  /**
   * Simulating a service with HTTP that returns Observables
   * You probably want to remove this and do all requests in a service with HTTP
   */


  @Input() client:any

  columns: TableColumn<PiecesJointe>[] = [
    {
      label: 'Type',
      property: 'type_piecejointe',
      type: 'text',
      visible: true,
      cssClasses: ['font-medium']
    },
    {
      label: 'Titre',
      property: 'titre',
      type: 'text',
      visible: true,
      cssClasses: ['font-medium']
    },
    { label: 'Nom fichier',
      property: 'filename',
      type: 'text',
      visible: true ,
      cssClasses: ['font-medium']
    },
    /*
    { label: 'Chemin',
      property: 'path',
      type: 'text',
      visible: true,
      cssClasses: ['font-medium'] },
    */
    { label: 'Actions', property: 'actions', type: 'button', visible: true }
  ];
  pageSize = 10;
  pageSizeOptions: number[] = [5, 10, 20, 50];
  dataSource!: MatTableDataSource<PiecesJointe>;
  selection = new SelectionModel<PiecesJointe>(true, []);
  searchCtrl = new UntypedFormControl();

  labels = aioTableLabels;

  @ViewChild(MatPaginator, { static: true }) paginator?: MatPaginator;
  @ViewChild(MatSort, { static: true }) sort?: MatSort;

  private readonly destroyRef: DestroyRef = inject(DestroyRef);

  constructor(private dialog: MatDialog ,private serviceHttpPJ:PiecesJointesHttpService,private tokenService:TokenService ) {}

  get visibleColumns() {
    return this.columns
      .filter((column) => column.visible)
      .map((column) => column.property);
  }

  ngOnInit() {

    this.dataSource = new MatTableDataSource();

    this.data$.pipe(filter<PiecesJointe[]>(Boolean)).subscribe((contacts) => {
      this.listOfPjs = contacts;
      this.dataSource.data = this.client.piecejointes;
    });

    this.searchCtrl.valueChanges
      .pipe(takeUntilDestroyed(this.destroyRef))
      .subscribe((value) => this.onFilterChange(value));
  }

  ngAfterViewInit() {
    if (this.paginator) {
      this.dataSource.paginator = this.paginator;
    }

    if (this.sort) {
      this.dataSource.sort = this.sort;
    }
  }

  createPj() {
    this.dialog
      .open(PieceJointeModalComponent, {
        data: {defaults:new PiecesJointe(), client:this.client}
        //width: '100vw',
      })
      .afterClosed()
      .subscribe((p_j: any) => {
        if (p_j) {
          try{
            this.dataSource.data = p_j ;
            this.dataSource._updateChangeSubscription();
          }catch(e){
            // this.contacts = []
            // this.contacts.unshift(new Contact(product));
          }
          // this.subject$.next(this.contacts);
          this.addChangeEvent()
        }
      });
  }

  updatePieceJointe(product: PiecesJointe) {
    // this.dialog
    //   .open(PieceJointeModalComponent, {
    //     data: {defaults:product, client:this.client}
    //     //width: '100vw',
    //   })
    //   .afterClosed()
    //   .subscribe((p_j) => {
    //     if (p_j) {
    //       try{
    //         this.dataSource.data = p_j ;
    //         this.dataSource._updateChangeSubscription();
    //       }catch(e){
    //         // this.contacts = []
    //         // this.contacts.unshift(new Contact(product));
    //       }
    //       // this.subject$.next(this.contacts);
    //       this.addChangeEvent()
    //     }
    //   });
  }

  deletePieceJointe(p_j: PiecesJointe) {
    if(confirm('are you sure to delete ?'))


    if(isObjectIdMongoose(this.client?._id)){
      showLoading()
      let set_fileUpload:any = {
        "_id":          this.client?._id , //"660d30f4fb544d3880beab69",
        "id_file":      p_j._id , //"660d30f4fb544d3880beab69",
        "code_societe": this.tokenService.getCodeSociete() , //"s02",
        "table":        enum_table_piecejointe.CLIENT , //"clients",
      };
      this.serviceHttpPJ.delete_file(set_fileUpload).subscribe((res) => {
        hideLoading()
        if(res.OK){
          this.dataSource.data = res.RESULTAT ;
          this.dataSource._updateChangeSubscription();

        }else{
          showAlertError('Erreur!', res.RESULTAT);
        }
      });
    }

    this.addChangeEvent()
  }

  downloadPieceJointe(p_j: PiecesJointe) {
    if(confirm('are you sure to download ?'))


    if(isObjectIdMongoose(this.client?._id)){
      showLoading()
      let set_fileUpload:any = {
        "_id":          this.client?._id , //"660d30f4fb544d3880beab69",
        "id_file":      p_j._id , //"660d30f4fb544d3880beab69",
        "code_societe": this.tokenService.getCodeSociete() , //"s02",
        "table":        enum_table_piecejointe.CLIENT , //"clients",
      };
      this.serviceHttpPJ.download_file(set_fileUpload).subscribe((res) => {
        hideLoading()
        if(res.OK){
          //this.showPdf()
          const linkSource = 'data:application/octet-stream;base64,' +  res.RESULTAT.fichier_content ;
          // '';
          const downloadLink = document.createElement('a');
          const fileName = res.RESULTAT.titre+'.'+res.RESULTAT.mimetype ;

          downloadLink.href     = linkSource;
          downloadLink.download = fileName;
          downloadLink.click();

        }else{
          showAlertError('Erreur!', res.RESULTAT);
        }
      });
    }

    this.addChangeEvent()
  }

  showPdf() {
    const linkSource =
      'data:application/octet-stream;base64,' +  'c3Nzc3Nzcw==' ;
     // '';
    const downloadLink = document.createElement('a');
    const fileName = 'sample.txt';

    downloadLink.href = linkSource;
    downloadLink.download = fileName;
    downloadLink.click();
  }

  deleteSelectPJ(contacts: PiecesJointe[]) {
    // contacts.forEach((c) => this.deleteProduct(c));
  }

  onFilterChange(value: string) {
    if (!this.dataSource) {
      return;
    }
    value = value.trim();
    value = value.toLowerCase();
    this.dataSource.filter = value;
  }

  toggleColumnVisibility(column: TableColumn<PiecesJointe>, event: Event) {
    event.stopPropagation();
    event.stopImmediatePropagation();
    column.visible = !column.visible;
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.dataSource.data.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected()
      ? this.selection.clear()
      : this.dataSource.data.forEach((row) => this.selection.select(row));
  }

  trackByProperty<T>(index: number, column: TableColumn<T>) {
    return column.property;
  }

}

